import "../../App.css";
import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Container, Table } from "react-bootstrap";

const ShowBasicsTools = () => {
  const mission_id = JSON.parse(window.sessionStorage.getItem("mission"))
    .mission_id;
    
    useEffect(() => {
     
    Axios.get("/basics/" + mission_id).then((response) => {
      const tempArray = [];
      tempArray.push(response.data);
      setBasicsList(tempArray);
      console.log("tempArray", tempArray);
      console.log("basicsList", basicsList);
    });
  }, []);

  const [basicsList, setBasicsList] = useState([]); 


  



  return (
    <div>
      <Container>
        <div style={{ textAlign: "center" }}>
          <b>Mission Basics Table With Tools</b>
        </div>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Tool ID</th>
              <th>Tool Name</th>
              <th>Tool Category</th>
               <th>Tool Description </th>
              <th>Image</th>
              
            </tr>
          </thead>
          <tbody>
           
            {basicsList.map((data, index) => (
              <tr key={index}>
                <td>{data.tool_details.tool_id}</td>
                <td>{data.tool_details.tool_name}</td>
                <td>{data.tool_details.tool_category}</td>
                <td>{data.tool_details.tool_description}</td>
               
                <td>
                  <img
                    src={data.tool_details.tool_image}
                    style={{ width: "100px" }}
                    alt={data.tool_details.tool_name}
                  ></img>
                </td>
                
              </tr>
            
            ))}
          </tbody>
        </Table>
      </Container>
    </div>
  );
};

export default ShowBasicsTools;
