import { useCallback } from "react";
import useUploadyContext from "./useUploadyContext";

var useAbortBatch = function () {
  var context = useUploadyContext();
  return useCallback(function (id) {
    return context.abortBatch(id);
  }, [context]);
};

export default useAbortBatch;
