import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Tabs from "../../components/Tabs";
import "../../App.css";
import MissionEquip from "../Mission/MissionEquip"
import EquipDetails from "../Equipment/EquipDetails";
import MissionTools from "./MissionTools";
import MissionStage from "../Stage/MissionStage";
import Axios from "axios";
import StageCatalog from "../Stage/StageCatalog";
import Logo from "../../Images/transp_amtilogo.png";

function CreateMission() {
  const [basicsList, setBasicsList] = useState();

  useEffect(() => {
    const missionVar = window.sessionStorage.getItem("mission");
    if (!missionVar) return;
    const mission_id = JSON.parse(missionVar)
      .mission_id;
    Axios.get("/basics/" + mission_id).then((response) => {
      setBasicsList(response.data);
    });
  }, []);

  return (
       <>
      <h1
        className="PageHead"
        style={{ marginBottom: 10, width: "12em", padding: "5px" }}
      >
        Add Mission Details
      </h1>
      {basicsList ? 
      <Container style={{ width: "95%" }}>
        <div>
          <div className="missionInfo ">
            <span style={{ marginLeft: "10px" }}>
              <b>Mission ID:</b> {basicsList.mission_id}{" "}
            </span>{" "}
            <span style={{ marginLeft: "10px" }}>
              <b>Name:</b> {basicsList.name}
            </span>
           
            <span style={{ marginLeft: "10px" }}>
              <b>Description:</b> {basicsList.description}
            </span>
          </div>
          <Tabs>
          <div label="Welcome" className="textBlock">
          <img
        src={Logo}
        alt="amtil logo"
        style={{
          width: "20%",
          display: "flex",
          marginLeft: "35%",
          marginRight: "35%",
          justifyContent: "center",
          textAlign: "center",
        }}
      ></img>
            <p className="textBlock"> Welcome to your Mission Builder Dashboard! Click the tabs above to add the elements needed for your mission. The mission will automatically be saved and can be viewed/edited/deleted via the "View Missions" section. If you notice something is missing, needs improvement, or is just plain wonky - be sure to use the 'Give Feedback' page and let us know! As with all things at AMTIL, we are in a constant iteration toward a better process and product. 

            Let's do this!</p>
           </div>
            <div label="Tools">
             
              <MissionTools />
            </div>
            <div label="Equipment">
            <MissionEquip />
            </div>
            <div label="Stage">
            <MissionStage />
            </div>
            <div label="Team">
              <em>Team</em>
            </div>
            <div label="Storyboard"></div>
            <div label="Procedural Steps">Steps</div>
            <div label="Quiz Questions">Quiz Questions</div>

            <div label="Styles/Color">Style and Color Notes</div>
            <div label="Media/Documents">Media Documents</div>
            <div label="FAA References">FAA References</div>
          </Tabs>
        </div>
      </Container> : <div>No Mission Selected</div>}
    </>
  );
}

export default CreateMission;
