import React, { useState } from "react";
import Axios from "axios";
import "../../App.css";
import DatePicker from 'react-date-picker';
// import ShowBasics from "../pages/ShowBasics";

Axios.defaults.withCredentials = true;


export default function Basics() {
  const [status, setStatus] = useState("");
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [summary, setSummary] = useState("");
  const [notes, setNotes] = useState("");
  const [start_date, setStartDate] = useState("");
  const [end_date, setEndDate] = useState("");
  const [cover_img, setCoverImg] = useState("");
  const [description, setdescription] = useState("");

    const mission_basics = () => {
    Axios.post("/basics", {
      status: status,
      name: name,
      category: category,
      summary: summary,
      notes: notes,
      start_date: start_date,
      end_date : end_date,
      cover_img : cover_img,
      description :description,

    }).then((response) => {
      console.log(response);
      console.log(response.data.name);
    });
  };

    return (
    <div className="container" >
      <h3 className="PageHead">Mission Basics</h3>
      <div className="inputBox" >

        <div> <label style={{marginRight: "5px"}}>Start Date:{"     "} </label>
        <DatePicker
          onChange={ (date) => {
            setStartDate(date)
          }}
          value={start_date}
                 />{"       "}
        <label style={{marginRight: "5px", marginLeft: "10px"}}>  End Date: {"     "}</label>
         <DatePicker
          onChange={ (date) => {
            setEndDate(date)
          }}
          value={end_date}
        /></div>

        {/* <label>Name</label> */}
        <input
        placeholder="Name"
          type="text"
          style={{margin: 5}}
          onChange={(e) => {
            setName(e.target.value);
          }}
        />

       <select id="Status" onChange={(e) => {
              setStatus(e.target.value);
            }}>
            <option value="Brainstorming">Brainstorming</option>
            <option value="Planning">Planning</option>
            <option value="Development">Development</option>
            <option value="Review">Review</option>
            <option value="Revision">Revision</option>
            <option value="Testing">Testing</option>
            <option value="Production">Production</option>
            <option value="Wrenches">Completed</option>
            <option value="Handles">Handles</option>
            <option value="Power Tools">Power Tools</option>
            <option value="Other">Other</option>

          </select>

        <input
        placeholder="Status"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setStatus(e.target.value);
          }}
        />
        {/* <label>category</label> */}
        <input
        placeholder="Category"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setCategory(e.target.value);
          }}
        />
        {/* <label>summary</label> */}
        <input
        placeholder="Summary"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setSummary(e.target.value);
          }}
        />
        {/* <label>notes</label> */}
        <input
        placeholder="Notes"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setNotes(e.target.value);
          }}
        />


        {/* <label>cover_img</label> */}
        <input
        placeholder="Cover Image URL"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setCoverImg(e.target.value);
          }}
        />
        {/* <label>description</label> */}
        <input
        placeholder="Description"
        style={{margin: 5}}
          type="text"
          onChange={(e) => {
            setdescription(e.target.value);
          }}
        />
        <button onClick={mission_basics}> Save</button>
      </div>

    </div>
  );
}
